&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;38&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;2.&lt;/span&gt; 算术左移&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;&amp;lt;&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt;符号位不变低位补&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;39&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;3.&lt;/span&gt; &lt;span class=&#34;token operator&#34;&gt;&gt;&gt;&gt;&lt;/span&gt;逻辑右移也叫无符号右移运算规则是&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt;低位溢出，高位补&lt;span class=&#34;token number&#34;&gt;0&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;40&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token number&#34;&gt;4.&lt;/span&gt; 特别说明&lt;span class=&#34;token operator&#34;&gt;:&lt;/span&gt;没有&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&amp;lt;&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&lt;/span&gt;符号&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;41&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;42&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token keyword&#34;&gt;int&lt;/span&gt; a&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;&gt;&gt;&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt; &lt;span class=&#34;token comment&#34;&gt;// 1 =&gt; 00000001 =&gt; 00000000 本质 1 / 2 / 2 = 0&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;43&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token keyword&#34;&gt;int&lt;/span&gt; c&lt;span class=&#34;token operator&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;1&lt;/span&gt;&lt;span class=&#34;token operator&#34;&gt;&amp;lt;&amp;lt;&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;2&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt; &lt;span class=&#34;token comment&#34;&gt;// 1 =&gt; 00000001 =&gt; 00000100 本质 1 * 2 * 2 = 4&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;p&gt;Java 数据类型有基本数据类型和引用数据类型&lt;/p&gt;
&lt;h1 id=&#34;基本数据类型&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#基本数据类型&#34;&gt;#&lt;/a&gt; 基本数据类型&lt;/h1&gt;
&lt;h2 id=&#34;整数类型&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#整数类型&#34;&gt;#&lt;/a&gt; 整数类型&lt;/h2&gt;
&lt;ul&gt;
&lt;li&gt;byte [字节] 1 字节&lt;br /&gt;
 short [短整型] 2 字节&lt;br /&gt;
 int [整型] 4 字节&lt;br /&gt;
 long [长整型] 8 字节&lt;/li&gt;
&lt;/ul&gt;
&lt;h2 id=&#34;浮点数类型&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#浮点数类型&#34;&gt;#&lt;/a&gt; 浮点数类型&lt;/h2&gt;
&lt;ul&gt;
&lt;li&gt;单精度 float 4 字节&lt;br /&gt;
双精度 double 8 字节&lt;/li&gt;
&lt;/ul&gt;
&lt;pre&gt;&lt;code class=&#34;language-Java&#34;&gt;//Java 的浮点型常量(具体值)默认为double型，声明float型常量，须后加‘f’或‘F&#39;
float num1 = 1.1; //错
float num2 = 1.1F; //对
double num3 = 1.1; //对
double num3 = 1.1f; //对
double num5 = .123; //等价于0.123
System.out.println(5.12e2);//512.0
System.out.println(5.12E-2); //0.0512
//浮点数使用陷阱: 2.7和8.1/3比较
double num11 = 2. 7 ;
double num12 = 8.1 / 3; //2.7
System.out.println(num11);/ /2.7
System.out.println(num12);//接近2.7的一个小数，而不是2.7 


//得到一个重要的使用点:当我们对运算结果是小数的进行相等判断是，要小心
//应该是以两个数的差值的绝对值，在某个精度范围类判断
if( num11 == num12) &amp;#123;
    System.out.println(&amp;quot;相等&amp;quot; );
&amp;#125;
//正确的写法
if (Math。abs (num11 - num12) &amp;lt; 0. 000001)&amp;#123;
    System.out.println(&amp;quot;差值非常小，到我的规定精度，认为相等...&amp;quot;);
&amp;#125;

&lt;/code&gt;&lt;/pre&gt;
&lt;h2 id=&#34;字符类型&#34;&gt;&lt;a class=&#34;anchor&#34; href=&#34;#字符类型&#34;&gt;#&lt;/a&gt; 字符类型&lt;/h2&gt;
